import React, { useState, useEffect } from 'react'
import axios from "./axios";

const base_url = `https://image.tmdb.org/t/p/original/`;

function Row({ title, fetchUrl }) {
  const [movies, setMovies] = useState([]);

  // A snippet of cade which runs based on a specific condition
  useEffect(() => {
    // if [] run only once when the row loads and don't run again
    // async functions are used to call outside data 
    async function fetchData() {
      const request = await axios.get(fetchUrl);
      setMovies(request.data.results);
      return request;
    }
    fetchData();
  }, [fetchUrl]);



  return (
    <div className="row">
      <h2>{title}</h2>

      <div className="row__posters">
        {/* several row__poster(s) */}

        {movies.map(movie => (


          <img scr={`${base_url}${movie.poster_path}`} alt={movie.name} />
        ))}

      </div>
    </div>
  )
}

export default Row
